import React from 'react';
import Styles from './Ball.module.scss';

function Ball(props) {
    const show = props.message.length ? "show" : "";

    return ( <
        div className = {
            Styles.ball
        } >
        <
        div className = {
            [Styles.fortune, show].join(" ")
        } > {
            props.message
        } <
        /div> <
        /div>
    )
}

export default Ball;
